import React,{useEffect, useState} from 'react';
import { createTodo, getTodos } from '../api/todo';

const ToDoComponent = () => {
    const [word,setWord] = useState()
    const [list,setList] = useState()

    const onClickCreateTodo =(todos)=>{
        createTodo(todos).then((res)=>{
            setList(list+[todos])
        })
        .catch((err) => {
            console.log(err)
            window.alert("createTodo error")
        })
    }
    const onClickgetTodos =()=>{
        getTodos().then((res)=>{
           setList(res.data)
        })
        .catch((err) => {
            console.log(err)
            window.alert("getTodo error")
        })
    }
    useEffect(()=>{
        onClickgetTodos()
        console.log(list)
     },[])

    return (
        <div>
            <div>TODOLIST</div>
            <input onChange={(e)=>setWord(e.target.value)}/>
            <div onClick={()=>onClickCreateTodo(word)}>추가</div>
            <div>
             {
                list? list.map((e,index) => {
                    return (
                        <div key={index}>
                           {e.todo}
                        </div>
                    )
                }) : ""
             }
            </div>
        </div>
    );
};

export default ToDoComponent;
