import React, { useState,useCallback } from 'react';
import { useNavigate } from 'react-router-dom'
import styled from 'styled-components';

const SignUpComponent = (props) => {

    const navigate = useNavigate();
    const [email, setEmail] = useState('')
    const [emailMessage, setEmailMessage] = useState('')
    const [isEmail, setIsEmail] = useState(false)
    const [emailColor,setEmailColor] = useState()
    const [password, setPassword] = useState('')
    const [passwordMessage, setPasswordMessage] = useState('')
    const [isPassword, setIsPassword] = useState(false)
    const [passwordColor,setPasswordColor] = useState()



    const onChangeEmail = useCallback((e) => {
        const emailRegex =
          /([\w-.]+)@/
        const emailCurrent = e.target.value
        setEmail(emailCurrent)

        if (!emailRegex.test(emailCurrent)) {
          setEmailMessage('이메일에는 @가 포함되어야 합니다')
          setIsEmail(false)
          setEmailColor("red")
        } else {
          setEmailMessage('올바른 이메일 형식입니다')
          setIsEmail(true)
          setEmailColor("green")
        }
      }, [])
    const onChangePassword = useCallback((e) => {
        const passwordRegex = /([\w-.]+).{7,25}$/
        const passwordCurrent = e.target.value
        setPassword(passwordCurrent)
        if (!passwordRegex.test(passwordCurrent)) {
          setPasswordMessage('비밀번호 8자리 이상 입력해주세요')
          setIsPassword(false)
          setPasswordColor("red")

        } else {
          setPasswordMessage('안전한 비밀번호입니다')
          setIsPassword(true)
          setPasswordColor("green")
        }
      }, [])
    return (
        <div>
            <div>SIGNUP</div>
            <div>이메일</div>
            <input onChange={onChangeEmail} placeholder='이메일을 입력해주세요'/>
            {email.length > 0 &&<span style={{color:`${emailColor}`}} className={`message ${isEmail ? 'success' : 'error'}`}>{emailMessage}</span>}
            <div>비밀번호</div>
            <input  type='password' onChange={onChangePassword} placeholder='비밀번호를 입력해주세요'/>
            {password.length > 0 && (<span style={{color:`${passwordColor}`}}className={`message ${isPassword ? 'success' : 'error'}`}>{passwordMessage}</span>)}
            <ButtonWrap>            
              <div onClick={()=>navigate("/todo")}>회원가입</div>
              <div onClick={()=>props.setLoginOrSignUp(false)}>취소</div>
            </ButtonWrap>

        </div>
    );
};

const ButtonWrap = styled.div`
display:flex;
justify-content:space-between;
`

export default SignUpComponent;
