import React,{useEffect, useState} from 'react';
import { createTodo, getTodos } from '../api/todo';
import ToDoContext from "../context/ToDoContext";
import ToDoInput from '../components/todo/ToDoInput';
import ToDoList from '../components/todo/ToDoList';
import styled from 'styled-components';
const TodoListPage = () => {
    const [word,setWord] = useState()
    const [list,setList] = useState()

    const onClickCreateTodo =(todos)=>{
        createTodo(todos).then((res)=>{
            setList([...list,todos])
        })
        .catch((err) => {
            console.log(err)
            window.alert("createTodo error")
        })
    }
    const onClickgetTodos =()=>{
        getTodos().then((res)=>{
           res.data.sort((a,b)=>b-a)
           setList(res.data)
        })
        .catch((err) => {
            console.log(err)
            window.alert("getTodo error")
        })
    }
    useEffect(()=>{
        onClickgetTodos()
        console.log(list)
     },[])

    return (
          <div>
           <Header></Header>
            <ToDoContext>
                <ToDoInput/>
                <ToDoList/>

            </ToDoContext>
          </div>
    );
};
const Header =styled.div`
font-size:30px;

`

export default TodoListPage;
