import React,{useEffect, useState} from 'react';
import { createTodo, getTodos } from '../api/todo';

const ToDoComponent = () => {
    const [word,setWord] = useState()
    const [list,setList] = useState()
    useEffect(()=>{
       getTodos()
    },[list])

    const onClickCreateTodo =(todos)=>{
        createTodo(todos).then((res)=>{
           setList(res.data)
        })
        .catch((err) => {
            console.log(err)
            window.alert("createTodo error")
        })
    }
    const getTodos =()=>{
        getTodos().then((res)=>{
           console.log(res)
        })
        .catch((err) => {
            console.log(err)
            window.alert("getTodo error")
        })
    }
    return (
        <div>
            <div>TODOLIST</div>
            <input onChange={(e)=>setWord(e.target.value)}/>
            <div onClick={()=>onClickCreateTodo(word)}>추가</div>
            <div>

            </div>
        </div>
    );
};

export default ToDoComponent;
